mapper.update:
UPDATE short_weather
SET TMN = #{TMN}, TMX = #{TMX}, SKY = #{SKY}, POP = #{POP}, PTY = #{PTY}
WHERE areacode = #{areacode.areacode}
  AND fcstDate = #{fcstDate}
  AND fcstTime = #{fcstTime}

mapper.select:
SELECT *
FROM short_weather
WHERE areacode = #{areacode.areacode}
  AND fcstDate = #{fcstDate}
  AND fcstTime = #{fcstTime}
